import React, { useState, useEffect } from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import axios from 'axios';

function ModalStyle({ flag, close, accept }) {
  const [lastEventData, setLastEventData] = useState(null);

  useEffect(() => {
    // Make an API call to fetch the last event data
    axios.get('http://your-api-endpoint-for-last-event')
      .then((response) => {
        setLastEventData(response.data); // Set the last event data in the state
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  }, []); // Empty dependency array to run this effect once on component mount

  const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 700,
    bgcolor: 'background.paper',
    boxShadow: 24,
    p: 4,
  };

  return (
    <div>
      <Modal
        open={flag}
        onClose={close}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <Typography id="modal-modal-title" variant="h6" component="h2"></Typography>
          <center><label className="title">Payment Sent!</label></center>
          <Typography id="modal-modal-title" variant="h6" component="h2">
            <center> A {lastEventData?.value} USD payment has been sent to the declared payment pointer ID: {lastEventData?.id}</center>
          </Typography>
          <Typography id="modal-modal-description" sx={{ mt: 2 }}>
            <center>Sent amount: {lastEventData?.value} USD</center>
            <center>Status: {lastEventData?.state}</center>
            <center>Payment ID: {lastEventData?.id}</center>
            <center>Metadata {lastEventData?.Brand ? lastEventData?.Brand : "Not found"}</center>
          </Typography>
          <Box
            component="img"
            sx={{
              height: 300,
              width: 350,
            }}
            pt={2}
            alt="image for product"
            src={lastEventData?.image ? lastEventData?.image : 'https://via.placeholder.com/300/09f/fff.png'}
          />
          <Stack spacing={2} direction="row" justifyContent="flex-end">
            <Button
              variant="contained"
              style={{ backgroundColor: "#8bc34a" }}
              onClick={accept}
            >Close!</Button>
            <Button variant="contained" onClick={close} style={{ backgroundColor: "#e91e63" }}>More bottles?</Button>
          </Stack>
        </Box>
      </Modal>
    </div>
  );
}

export default ModalStyle;
