import React, { useState, useEffect } from 'react';
import './Home.css';
import Stepper from '@mui/material/Stepper';
import Step from '@mui/material/Step';
import StepLabel from '@mui/material/StepLabel';
import Box from '@mui/material/Box';
import ButtonStyle from '../components/button';
import { useNavigate } from 'react-router-dom';
import BalanceDisplay from './BalanceDisplay.jsx';
import qrcode from '../assets/qrcode.jpeg';

function Home() {
  const steps = ['SCAN', 'PACK', 'RECEIVE'];
  const navigate = useNavigate();

  const routeChange = () => {
    navigate('/step1');
  }

  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [balance, setBalance] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch data from the first endpoint
        const response1 = await fetch('http://localhost:3500/scanned-data');
        const data1 = await response1.json();
        setData(data1);
        setIsLoading(false);

        if (data1 && data1.balance !== undefined) {
          setBalance(data1.balance);
        }

        // Fetch data from the additional endpoint
        const response2 = await fetch('http://localhost:3500/scanned-data-count');
        const data2 = await response2.json();

        if (data2 && data2.balance !== undefined) {
          setBalance(data2.balance);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const formattedData = data.join(', ');

  return (
    <div className="App">
      <div className="container">
        <label className="subtitle">Acme Recycling Company!</label>
        <BalanceDisplay balance={data2.balance} />
        <img src={qrcode} className="App-qr" alt="logo" />

        <Box sx={{ width: '80%' }}>
          <Stepper activeStep={0} alternativeLabel>
            {steps.map((label, index) => (
              <Step
                key={index}
                sx={{
                  '& .MuiStepLabel-root .Mui-completed': {
                    color: 'secondary.dark',
                  },
                  '& .MuiStepLabel-label.Mui-completed.MuiStepLabel-alternativeLabel': {
                    color: 'grey.500',
                  },
                  '& .MuiStepLabel-root .Mui-active': {
                    color: '#8bc34a',
                  },
                  '& .MuiStepLabel-label.Mui-active.MuiStepLabel-alternativeLabel': {
                    color: 'common.black',
                  },
                  '& .MuiStepLabel-root .Mui-active .MuiStepIcon-text': {
                    fill: 'black',
                  },
                }}
              >
                <StepLabel>{label}</StepLabel>
              </Step>
            ))}
          </Stepper>
        </Box>
        <ButtonStyle Text="Start!" onClick={routeChange} />

        {isLoading ? (
          <p>Loading data...</p>
        ) : (
          <div>
            <p>Fetched Data: {formattedData}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default Home;
