import React, { useState, useEffect } from 'react';
import Modal from '@mui/material/Modal';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import axios from 'axios';

function App() {
  const [flag, setFlag] = useState(false);
  const [lastEventData, setLastEventData] = useState(null);

  useEffect(() => {
    // Make an API call to fetch the last event data
    axios.get('http://your-api-endpoint-for-last-event')
      .then((response) => {
        setLastEventData(response.data); // Set the last event data in the state
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  }, []); // Empty dependency array to run this effect once on component mount

  const openModal = () => {
    setFlag(true);
  }

  const closeModal = () => {
    setFlag(false);
  }

  return (
    <div>
      <button onClick={openModal}>Open Modal</button>
      <Modal
        open={flag}
        onClose={closeModal}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box
          sx={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            width: 700,
            bgcolor: 'background.paper',
            boxShadow: 24,
            p: 4,
          }}
        >
          <Typography id="modal-modal-title" variant="h6" component="h2">Payment Sent!</Typography>
          {lastEventData ? (
            <div>
              <Typography id="modal-modal-description" sx={{ mt: 2 }}>
                A {lastEventData.value} USD payment has been sent to the declared payment pointer ID: {lastEventData.id}
                <br />
                Sent amount: {lastEventData.value} USD
                <br />
                Status: {lastEventData.state}
                <br />
                Payment ID: {lastEventData.id}
                <br />
                Metadata {lastEventData.Brand ? lastEventData.Brand : "Not found"}
              </Typography>
              <Box
                component="img"
                sx={{
                  height: 300,
                  width: 350,
                }}
                pt={2}
                alt="image for product"
                src={lastEventData.image ? lastEventData.image : 'https://via.placeholder.com/300/09f/fff.png'}
              />
              <Stack spacing={2} direction="row" justifyContent="flex-end">
                <Button
                  variant="contained"
                  style={{ backgroundColor: "#8bc34a" }}
                  onClick={closeModal}
                >Close!</Button>
              </Stack>
            </div>
          ) : (
            <div>Loading...</div>
          )}
        </Box>
      </Modal>
    </div>
  );
}

export default App;
