import "./Scan.css";
import ButtonStyle from "../components/button";
import ModalStyle from "../components/Modal";
import React, { useState } from "react";
import { Grid } from "@material-ui/core";
import { useNavigate } from "react-router-dom";
import BarcodeScannerComponent from "react-qr-barcode-scanner";
import products from '../products.json';

// const URL = "https://backend-products-scan.onrender.com:3500";
const URL = 'http://localhost:3500';

function Scan() {
  const initialData = products;
  const [data, setData] = useState("No result");
  const [producto, setProducto] = useState({});
  const [open, setOpen] = useState(false);
  const [filteredData, setFilteredData] = useState(initialData);
  let navigate = useNavigate();

  const handleAccept = () => {
    // Your logic here
  };

  const handleScanWebCam = (result) => {
    if (result) {
      const resultText = result.toString();
      setData(resultText);
      if (!open) {
        getProduct(resultText);
      }

      // Save the scanned barcode to local storage
      localStorage.setItem('scanned_barcodes', JSON.stringify(resultText));

      // Call the function to parse and send the data
      parseAndSendToEndpoint(resultText);
    }
  };

  const getProduct = (id) => {
    const foundElement = filteredData.find(item => item.Id === id);

    if (foundElement) {
      setProducto(foundElement);
      setOpen(true);
    } else {
      // Element with the given id was not found
      // console.log('Element not found');
    }
  };

  return (
    <div className="App">
      <div className="container">
        <label className="title">Step 1</label>
        <p>
          Scan a selected product listed on www.rezy.com, aim to the camera and
        </p>
        <p> Wait for the confirmation popup, once the bottle is recognized a grant will be generated</p>

        <Grid container style={{ width: window.innerWidth < 1000 ? "100%" : "25%" }}>
          <Grid item xl={12} lg={12} md={12} sm={12} xs={12}>
            <BarcodeScannerComponent
              width={400}
              height={400}
              facingMode="environment"
              onUpdate={(err, result) => {
                if (result) {
                  handleScanWebCam(result.text);
                  console.log(result);
                } else {
                  setData(".....Waiting for your bottle");
                }
              }}
            />
          </Grid>
        </Grid>

        <p>{data}</p>
        <ButtonStyle Text={"Scan!"}></ButtonStyle>
      </div>
      <ModalStyle
        flag={open}
        close={handleClose}
        product={producto}
        accept={handleAccept}
      ></ModalStyle>
    </div>
  );
}

export default Scan;
