import React, { useState, useEffect } from 'react';
import QrReader from 'react-qr-reader';
import { Grid } from '@material-ui/core';
import { useNavigate } from 'react-router-dom';
import ButtonStyle from '../components/button';
import ModalInfo from '../components/InfoModal';

function Token() {
  // ... (the rest of your component code)

  const handleScanWebCam = (result) => {
    if (result) {
      setData(`Your pack code is: ${result}`);
      setToken(result);

      // Create a JSON object with the result string
      const dataToSend = { result };

      // Send the data to the local backend
      fetch('http://127.0.0.1:3500/scan', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(dataToSend),
      })
        .then((response) => {
          if (response.ok) {
            // Handle a successful response from the backend
            // You can add your logic here
          } else {
            // Handle errors from the backend
            console.error('Error sending data to the backend');
          }
        })
        .catch((error) => {
          console.error('Error:', error);
        });
    }
  };

  // ... (the rest of your component code)

  return (
    <div className="App">
      {/* ... (the rest of your component JSX) */}
    </div>
  );
}

export default Token;
