import "./Scan.css";
import ButtonStyle from "../components/button";
import ModalStyle from "../components/Modal";
import React, { useState } from "react";
import QrReader from "react-qr-reader";
import { Grid } from "@material-ui/core";
import { useNavigate } from "react-router-dom";
import Barcode from "react-barcode";
import BarcodeScannerComponent from "react-qr-barcode-scanner";
import products from '../products.json'

const URL = "https://backend-products-scan.onrender.com:3500";
//const URL = 'http://localhost:3500'

function Scan() {
  const initialData = products
  const [data, setData] = useState("No result");
  const [producto, setProducto] = useState({});
  const [stopStream, setStopStream] = useState(false);
  const [filteredData, setFilteredData] = useState(initialData);

  let navigate = useNavigate();

  const [open, setOpen] = useState(false);
  const handleClose = () => {
    postToArduino("0");
    setOpen(false);
  };

  const postToArduino = (option) => {
    const headers = {
      "Content-Type": "text/html",
    };
    let url = `https://rezy.lat/esp-outputs-action.php?action=output_update&id=1&state=${option}`;
    fetch(url, { headers })
      .then((response) => {
        if (response.status === 200) {
          setData("Green led is on");
        } else {
          setData("Green led is off");
        }
      })
      .catch((err) => {
        console.log(err);
        setOpen(false);
      });
  };

  const handleAccept = () => {
    postToArduino("1");
    setTimeout(function () {
      postToArduino("0");
      setOpen(false);
      navigate("/step3");
    }, 2000);
  };

  const handleErrorWebCam = (error) => {
    console.log(error);
  };
  const handleScanWebCam = (result) => {
    if (result) {
      setData(result);
      if (!open) {
        getProduct(result);
      }
    }
  };

  const getProduct = (id) => {
    const foundElement = filteredData.find(item => item.Id === id);

    if (foundElement) {
      // Do something with the found element
      setProducto(foundElement);
      setOpen(true);
    } else {
      // Element with the given id was not found
      console.log('Element not found');
    }

    // const headers = {
    //   "Content-Type": "application/json",
    // };
    // fetch(`${URL}/${name}`, { headers })
    //   .then((response) => response.json())
    //   .then((data) => {
    //     setProducto(data[0]);
    //     setOpen(true);
    //   });
  };

  return (
    <div className="App">
      <div className="container">
        <label className="title">Step 1</label>
        <p>
          Scan a selected product listed on www.rezy.com , aim to the camera and
          wait for the confirmation popup
        </p>

        <Grid
          container
          style={{ width: window.innerWidth < 1000 ? "100%" : "25%" }}
        >
          <Grid item xl={12} lg={12} md={12} sm={12} xs={12}>
            <BarcodeScannerComponent
              width={500}
              height={500}
              // stopStream={stopStream}
              onUpdate={(err, result) => {
                if (result) {
                  handleScanWebCam(result.text)
                  // setData(result.text);
                  // setStopStream(true);
                } else {
                  setData("Not Found");
                }
              }}
            />
          </Grid>
        </Grid>

        <p>{data}</p>
        <ButtonStyle Text={"Scan!"}></ButtonStyle>
      </div>
      <ModalStyle
        flag={open}
        close={handleClose}
        product={producto}
        accept={handleAccept}
      ></ModalStyle>
    </div>
  );
}

export default Scan;
