import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import qrcode from '../assets/qrcode.jpeg';
import './Home.css';
import Stepper from '@mui/material/Stepper';
import Step from '@mui/material/Step';
import StepLabel from '@mui/material/StepLabel';
import Box from '@mui/material/Box';
import ButtonStyle from '../components/button';

function Home() {
  const [apiData, setApiData] = useState(''); // State to store API data
  const steps = ['SCAN', 'PACK', 'RECEIVE'];
  const navigate = useNavigate();

  useEffect(() => {
    // Make the API call when the component mounts
    axios.get('http://192.168.1.13:3031/?_data=routes%2Findex')
      .then((response) => {
        // Set the API data in the state
        setApiData(response.data);
      })
      .catch((error) => {
        console.error('Error fetching data from the API:', error);
      });
  }, []);

  const routeChange = () => {
    navigate('/step1');
  };

  return (
    <div className="App">
      <div className="container">
        <label className="title">
          Welcome to Rezy, an Interledger and Open Payments-based recycling program!
        </label>
        <p>
          Monetize your bottles made of "polyethylene terephthalate" (PET).
        </p>
        <p>My payment pointer is https://happy-life-bank-backend/recycler1</p>
        <img src={qrcode} className="App-qr" alt="QR Code" />
        {apiData && (
          <div>
            <p>API Data:</p>
            <pre>{JSON.stringify(apiData, null, 2)}</pre>
          </div>
        )}
        <p>3 easy steps</p>
        <Box sx={{ width: '80%' }}>
          <Stepper activeStep={0} alternativeLabel>
            {steps.map((label, index) => (
              <Step key={index}>
                <StepLabel>{label}</StepLabel>
              </Step>
            ))}
          </Stepper>
        </Box>
        <ButtonStyle Text="Start!" onClick={routeChange} />
      </div>
    </div>
  );
}

export default Home;
