  // State for the fetched data
  const [scannedDataCountMultiplied, setScannedDataCountMultiplied] = useState(null);

  // ...

  useEffect(() => {
    // Fetch data from the API endpoint when the component mounts
    fetch('http://localhost:3500/scanned-data-count-multiplied')
      .then((response) => {
        if (response.status === 200) {
          return response.json();
        } else {
          console.error('Error fetching data from the endpoint');
          return null;
        }
      })
      .then((data) => {
        // Update the state with the fetched data
        setScannedDataCountMultiplied(data);
      })
      .catch((error) => {
        console.error('Network error:', error);
      });
  }, []);

